import React, { useState } from "react"
import "../styles.css"
import { Button, Input } from "../../"

const EditableContactChip = ({...props}) => {
	const [inputData, setInputData] = {}

	return (
		<div className="ContactChip">
			New contact
			<Input id="name" label="Name" />
			<Input id="phonenum" label="Phone number" />
			<Input id="emailadd" label="Email address" />
			<Button onClick={ () => props.onSave() } text={ "Save" } />
			<Button onClick={ () => props.onCancel() } text={ "Cancel" } />
		</div>
	)
}

export { EditableContactChip }
