import React, { useState, useEffect } from "react"
import "./styles.css"
import Contacts from "./test"
import { ContactChip, Button, EditableContactChip } from "../../Components"

const StyledList = () => {

	const [contacts, setContacts] = useState({})
	useEffect(() => {
		const fetchContacts = async () => await setContacts(Contacts)
		fetchContacts()
	}, [])

	const [newContact, setNewContact] = useState(false)
	const handleAddContact = () => {
		setNewContact(true)
	}
	const finishAddingContact = () => {
		setNewContact(false)
	}
	
	return (
		<div className="List">
			<h1>Your Contacts</h1>
			<Button onClick={ handleAddContact } text={ "Add contact" } />
			<div className="ContactList">{
				newContact &&
					<EditableContactChip
						onSave={ finishAddingContact }
						onCancel={ setNewContact(false) }
						contact={ {name: "New Contact!", phone: {number: "", tag: ""}} } />
			}{
				Contacts.map((contact) => {
					return <ContactChip contact={ contact } />
				}) 
			}</div>
		</div>
	)
}

export { StyledList }
